// As the SVGs have starts and a particular format, there is one SVG per language
// so the SVGs are not translated. We just render the appropriate SVG given a locale.
// In addition to that, each component has a mobile and a desktop version, as texts
// are centered or aligned left given the screen size.
const componentsPerLanguage: Record<Locale, () => ReactElement> = {
  en: StakeAndEarnEnglish,
  es: StakeAndEarnSpanish,
  pt: StakeAndEarnPortuguese,
}

export const StakeAndEarn = function () {
  const locale = useLocale()
  const Component = componentsPerLanguage[locale]
  if (!Component) {
    throw new Error(`Missing "Stake and Earn" component for locale ${locale}`)
  }
  return (
    <div className="flex h-32 w-full items-center justify-center lg:relative lg:items-start lg:justify-start">
      {/* Trick designed by Nahuel so the overlay of the 4 components causes a particular blended color */}
      <Component />
      <Component />
      <Component />
      <Component />
    </div>
  )
}
